function navBehavior(){const o=document.querySelector(".hamburguer"),e=document.querySelector(".listado-opciones");o.addEventListener("click",()=>{o.classList.toggle("activo"),e.classList.toggle("show-menu")})}function scrollNav(){const o=document.querySelector(".seccion2"),e=document.querySelector(".seccion4"),c=document.querySelector(".seccion7"),t=document.querySelector(".seccion1"),n=document.querySelectorAll(".opcion"),r=document.querySelector("#logo-principal");n.forEach(t=>{t.addEventListener("click",()=>{const n=t.id;"sobre"===n?e.scrollIntoView({behavior:"smooth"}):"servicios"===n?o.scrollIntoView({behavior:"smooth"}):"contacto"===n&&c.scrollIntoView({behavior:"smooth"})})}),r.addEventListener("click",(function(o){t.scrollIntoView({behavior:"smooth"})}))}document.addEventListener("DOMContentLoaded",(function(o){navBehavior(),scrollNav()}));